PRIVATE_KEY=0xac0974bec39a17e36ba4a6b4d238ff944bacb478cbed5efcae784d7bf4f2ff80 # anvil default

function cutit() {
 grep 'contractAddress         0x[a-zA-Z0-9]\{40\}' | tr ' ' '\n' | tail -1
}

# deploy poseidon with 2 inputs
cast send --private-key $PRIVATE_KEY --create  | cutit

# Change address to the deployed contract address
#cast call 0x2279B7A0a67DB372996a5FaB50D91eAA73d2eBe6 --private-key $PRIVATE_KEY "poseidon(uint256[2])" [1,2]
